INSERT INTO vencedoresDOoscar(Year,Category,Nominee,Additional_Info,Won,ob1,ob2,ob3,ob4,ob5,FIELD11) VALUES ('2008 (81st)','Scientific and Technical (Scientific and Engineering Award)','To ERWIN MELZNER for the overall concept including the optical and cooling systems, VOLKER SCHUMACHER for the optical design, and TIMO M�LLER for the mechanical design, of the Arrimax 18/12 lighting fixture for use in motion picture production. With its choice of vari-focus and specular reflectors, the superior optical and mechanical design of this lighting fixture allows it to operate at 18,000 watts, producing unsurpassed light quality while its innovative cooling system keeps the housing safe to touch. [Lighting]',NULL,'YES',NULL,NULL,NULL,NULL,NULL,NULL);
