INSERT INTO vencedoresDOoscar(Year,Category,Nominee,Additional_Info,Won,ob1,ob2,ob3,ob4,ob5,FIELD11) VALUES ('2008 (81st)','Scientific and Technical (Scientific and Engineering Award)','To BRUNO COUMERT and JACQUES DEBIZE for the optical design, and DOMINIQUE CHERVIN and CHRISTOPHE REBOULET for the mechanical design, of the compact and lightweight Angenieux 15-40 and 28-76 zoom lenses for handheld motion picture photography. With focus and zoom functions that can be easily controlled by either the operator or focus puller while filming handheld, these lightweight zoom lenses demonstrate a very high degree of engineering, supporting both ease of use and quick interchange. [Lenses and Filters]',NULL,'YES',NULL,NULL,NULL,NULL,NULL,NULL);
