INSERT INTO vencedoresDOoscar(Year,Category,Nominee,Additional_Info,Won,ob1,ob2,ob3,ob4,ob5,FIELD11) VALUES ('2007 (80th)','Scientific and Technical (Special Awards)','To Jonathan Erland in recognition of his leadership and efforts toward identifying and solving the problem of High-Speed Emulsion Stress Syndrome in motion picture film stock. By coordinating and conducting tests for and with the industry, and by sharing results openly, Mr. Erland clearly demonstrated the value and effectiveness of independent research and industry-wide cooperation.',NULL,'YES',NULL,NULL,NULL,NULL,NULL,NULL);
