DROP TABLE IF EXISTS vencedoresDOoscar;
CREATE TABLE vencedoresDOoscar(
   id              int(13) NOT NULL AUTO_INCREMENT
  ,Year            VARCHAR(20) NOT NULL
  ,Category        VARCHAR(60) NOT NULL
  ,Nominee         VARCHAR(800) NOT NULL
  ,Additional_Info VARCHAR(180)
  ,Won             VARCHAR(200) NOT NULL
  ,ob1             VARCHAR(150)
  ,ob2             VARCHAR(65)
  ,ob3             VARCHAR(50)
  ,ob4             VARCHAR(50)
  ,ob5             VARCHAR(10)
  ,FIELD11         VARCHAR(10)
  ,PRIMARY KEY (id)
